import React, { useState } from 'react';
import './App.css';

const API_URL = 'http://localhost:5000/api';

function App() {
  const [config, setConfig] = useState({
    wallALength: 140,
    wallBLength: 120,
    rangeCenter: 75,
    sinkCenter: 60,
    hasRefPanels: false,
    panelSize: 3.0,
    hasDishwasher: true,
    dishwasherPosition: 'right'
  });

  const [results, setResults] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setConfig(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : 
              type === 'number' ? parseFloat(value) : value
    }));
  };

  const handleCalculate = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch(`${API_URL}/calculate`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(config)
      });

      const data = await response.json();

      if (data.success) {
        setResults(data.layout);
      } else {
        setError(data.error || 'Calculation failed');
      }
    } catch (err) {
      setError('Failed to connect to backend. Make sure the Python server is running.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Kitchen Cabinet Layout Calculator</h1>
        <p className="subtitle">POC v1.0 - Real Platform Edition</p>
      </header>

      <div className="container">
        <div className="config-section">
          <h2>Wall A: Refrigerator + Range</h2>

          <div className="input-group">
            <label>Wall Length (inches):</label>
            <input
              type="number"
              name="wallALength"
              value={config.wallALength}
              onChange={handleInputChange}
              min="60"
              max="300"
            />
          </div>

          <div className="input-group">
            <label>Range Center Position (inches from left):</label>
            <input
              type="number"
              name="rangeCenter"
              value={config.rangeCenter}
              onChange={handleInputChange}
              min="30"
              max="250"
            />
          </div>

          <div className="checkbox-group">
            <label>
              <input
                type="checkbox"
                name="hasRefPanels"
                checked={config.hasRefPanels}
                onChange={handleInputChange}
              />
              Has Refrigerator Panels
            </label>
          </div>

          {config.hasRefPanels && (
            <div className="input-group">
              <label>Panel Size:</label>
              <select
                name="panelSize"
                value={config.panelSize}
                onChange={handleInputChange}
              >
                <option value="0.75">0.75"</option>
                <option value="1.5">1.5"</option>
                <option value="3.0">3.0"</option>
              </select>
            </div>
          )}
        </div>

        <div className="config-section">
          <h2>Wall B: Sink + Dishwasher</h2>

          <div className="input-group">
            <label>Wall Length (inches):</label>
            <input
              type="number"
              name="wallBLength"
              value={config.wallBLength}
              onChange={handleInputChange}
              min="60"
              max="300"
            />
          </div>

          <div className="input-group">
            <label>Sink Center Position (inches from left):</label>
            <input
              type="number"
              name="sinkCenter"
              value={config.sinkCenter}
              onChange={handleInputChange}
              min="30"
              max="250"
            />
          </div>

          <div className="checkbox-group">
            <label>
              <input
                type="checkbox"
                name="hasDishwasher"
                checked={config.hasDishwasher}
                onChange={handleInputChange}
              />
              Include Dishwasher
            </label>
          </div>

          {config.hasDishwasher && (
            <div className="input-group">
              <label>Dishwasher Position:</label>
              <select
                name="dishwasherPosition"
                value={config.dishwasherPosition}
                onChange={handleInputChange}
              >
                <option value="right">Right of Sink</option>
                <option value="left">Left of Sink</option>
              </select>
            </div>
          )}
        </div>

        <button 
          className="calculate-btn"
          onClick={handleCalculate}
          disabled={loading}
        >
          {loading ? 'Calculating...' : 'Calculate Layout'}
        </button>

        {error && (
          <div className="error-message">
            <strong>Error:</strong> {error}
          </div>
        )}

        {results && (
          <div className="results">
            <h2>Layout Results</h2>

            <div className="wall-results">
              <h3>Wall A - Base Cabinets ({results.wallA.length}")</h3>
              <div className="cabinet-list">
                {results.wallA.items.map((item, index) => (
                  <div key={index} className="cabinet-item">
                    <span className="item-number">{index + 1}.</span>
                    <span className="item-position">[{item.position.toFixed(2)}"]</span>
                    <span className="item-sku">{item.sku}</span>
                    <span className="item-description">- {item.description}</span>
                  </div>
                ))}
              </div>
            </div>

            <div className="wall-results">
              <h3>Wall B - Base Cabinets ({results.wallB.length}")</h3>
              <div className="cabinet-list">
                {results.wallB.items.map((item, index) => (
                  <div key={index} className="cabinet-item">
                    <span className="item-number">{index + 1}.</span>
                    <span className="item-position">[{item.position.toFixed(2)}"]</span>
                    <span className="item-sku">{item.sku}</span>
                    <span className="item-description">- {item.description}</span>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
